void TestHwr::showPageCandidate(unsigned int index)
{
	if (index >= m_hwr.getCandidatePageCount())
	{
		std::string s(m_hwr.getCandidatePageSize(), '\n');
		printf("%s\n", (s + SEPERATOR_LINE).data());
		return;
	}

	std::string sAll;
	std::vector<std::string> candidates;
	m_hwr.getCandidateByPage(index, candidates);
	for (int i = 0; i != candidates.size(); ++i)
	{
		std::string can = candidates[i];
		sAll = sAll + std::to_string(i + 1) + ".\t" + can + "\n";
	}
#ifdef WIN32
	sAll = Encoding::utf8ToGb2312(sAll.data());
#endif

	sAll.insert(sAll.end(), m_hwr.getCandidatePageSize() - candidates.size(), '\n');
	printf("%s[page:%d/%d, total:%d]\n", (sAll + SEPERATOR_LINE).data(), m_curPage + 1, m_hwr.getCandidatePageCount(), m_hwr.getCandidateCount());
}
